        void ContexMenuKopyalaTasiIcinDegerAl(string OdaNo, string RezarvasyonBaslangicTarihi, System.Drawing.Color OdaRengi)
        {
            if (OdaRengi == Color.LightGreen)
            {
                MessageBox.Show("\t İŞLEM YAPILMAK İSTENEN ODA BOŞ !\nBOŞ OADAYA KOPYALA VEYA KES GİBİ İŞLEMLER YAPILAMAZ ! ", "DİKKAT !!!", MessageBoxButtons.OK, MessageBoxIcon.Stop); 
            }
            else if (OdaRengi == Color.Red)
            {
                //--------- MusteriBilgileri ve Oda Durumlari tablosundan kopyala
                bag.Open();
                OleDbDataAdapter adtr = new OleDbDataAdapter("select TC,Ad,Soyad,CepTel,OdaNumarasi,BaslangicRezarvasyonTarihi,BitisRezarvasyonTarihi,OdemeTuru,Ucret,MedeniHali,KanGrubu,BabaAdi,AnneAdi,DogumTarihi,DogumYeri,Cinsiyet,EvTel,IsTel,Fax,EMail,Meslek,EvAdresi,IsAdresi  from MusteriBilgileri where OdaNumarasi= '" + OdaNo + "' and BaslangicRezarvasyonTarihi='" + RezarvasyonBaslangicTarihi + "'order by TC desc", bag);
                OleDbDataAdapter adtr1 = new OleDbDataAdapter("select * from OdaDurumlari where OdaIsmi= '" + OdaNo + "' and GirisTarihi='" + RezarvasyonBaslangicTarihi + "'", bag);
                dtst.Clear();
                adtr.Fill(dtst, "MusteriBilgileri");
                dataGridView1.DataSource = dtst.Tables["MusteriBilgileri"];
                adtr1.Fill(dtst, "OdaDurumlari");
                dataGridView3.DataSource = dtst.Tables["OdaDurumlari"];
                bag.Close();
                
              }
            else if (OdaRengi==Color.Yellow)
            {
                //--------- Rezarvasyon ve OdaDurumlari tablosundan kopyala
                bag.Open();
                OleDbDataAdapter adtr = new OleDbDataAdapter("select * from Rezarvasyon where OdaIsmi= '" + OdaNo + "' and RezarvasyonBaslangicTarihi='" + RezarvasyonBaslangicTarihi + "'", bag);
                OleDbDataAdapter adtr1 = new OleDbDataAdapter("select * from OdaDurumlari where OdaIsmi= '" + OdaNo + "' and GirisTarihi='" + RezarvasyonBaslangicTarihi + "'", bag);
                dtst.Clear();
                adtr.Fill(dtst, "Rezarvasyon");
                dataGridView1.DataSource = dtst.Tables["Rezarvasyon"];
                adtr1.Fill(dtst, "OdaDurumlari");
                dataGridView3.DataSource = dtst.Tables["OdaDurumlari"];
                bag.Close();
               
             }
         }
       
