                kmt.CommandText = "select *  from Rezarvasyon where OdaIsmi= '" + OdaIsmiYolla + "' and RezarvasyonBaslangicTarihi='" + tarih1 + "'";
                OleDbDataReader oku = kmt.ExecuteReader();
                while (oku.Read())
                {
                    string MusteriNo = oku["MusteriNo"].ToString();
                    string Ad = oku["Adi"].ToString();
                    string Soyad = oku["Soyadi"].ToString();
                    string Aciklama1Bilgiler = oku["Aciklama1Bilgiler"].ToString();
