                    string CepTel = oku["CepTel"].ToString();
                    string EvTel = oku["EvTel"].ToString();
                    string IsTel = oku["IsTel"].ToString();
                    string Fax = oku["Fax"].ToString();
                    string EMail = oku["EMail"].ToString();
                    string OdaIsmi = oku["OdaIsmi"].ToString();
                    string RezarvasyonBaslangicTarihi = oku["RezarvasyonBaslangicTarihi"].ToString();
                    string RezarvasyonBitisTarihi = oku["RezarvasyonBitisTarihi"].ToString();
