     public void DoldurTarih2_LblOda1Tarih1(string tarih1)
     {

         if (Lblgun1.Text == tarih1)
         {
             bag.Open();
             kmt.Connection = bag;
             kmt.CommandText = "select *  from OdaDurumlari where OdaIsmi= '" + LblOda1.Text + "' and GirisTarihi='" + Lblgun1.Text + "'";
             OleDbDataReader oku = kmt.ExecuteReader();
             if (oku.Read())
             {
                 string TcKimlikNo = oku["TC"].ToString();
                 string Ad = oku["Adi"].ToString();
                 string Soyad = oku["Soyadi"].ToString();
                 string OdaDurumu = oku["OdaDurumu"].ToString();
                 if (OdaDurumu == "Rezerve")
                 {
                     LblOda1Tarih1.Text = "Adı: " + Ad + "\n" + "Soyadı: " + Soyad + "\n" + "\n\n" + "OdaDurumu: " + OdaDurumu;
                 }
                 else if (OdaDurumu == "dolu")
                 {
                     LblOda1Tarih1.Text = "TC: " + TcKimlikNo + "\n" + "Adı: " + Ad + "\n" + "Soyadı: " + Soyad;
                 }
                 else
                 {
                     LblOda1Tarih1.Text = "";
                 }
             }
             else
             {
                 LblOda1Tarih1.Text = "";
             }

             bag.Close();
         }
     }
