     private void frmMenu_KeyDown(object sender, KeyEventArgs e)
     {
         switch (e.KeyData)
         {
             case Keys.Escape: //esc tuşuna basıldığında çıkış yapmak istiyor Musun? sorusu gelsin
                 DialogResult sonuc;
                 sonuc = MessageBox.Show("PROGRAMDAN ÇIKMAK İSTEDİĞİNİZDEN, EMİN MİSİNİZ ? ", "DİKKAT !!!", MessageBoxButtons.YesNo, MessageBoxIcon.Warning);
                 if (sonuc == DialogResult.Yes)
                 {
                     Application.Exit();
                 }
                 break;
             default:
                 break;
         }

         if (tabControl1.SelectedIndex==0) //ana menü ise 
         {
             switch (e.KeyData)
             {
                 case Keys.F5: //ana menü sayfasında F5 kısayol tuşu ile sayfa yeniden yüklenir(yenile)
                     DoldurTarih1(Lblgun1.Text, Lblgun2.Text, Lblgun3.Text, Lblgun4.Text, Lblgun5.Text, Lblgun6.Text, Lblgun7.Text);  
                     break;
                 default:
                     break;
             }
         }
         else if (tabControl1.SelectedIndex == 1) //raporlar ise 
         {
             switch (e.KeyData)
             {
                 case Keys.F2: //Raporlar sayfasında F2 kısayol tuşu GÖRÜNTÜLE
                     Goruntule();
                     break;
                 default:
                     break;
             }
             switch (e.KeyData)
             {
                 case Keys.F4: //Raporlar sayfasında F4 kısayol tuşu YAZDIR
                     if (SetupThePrinting())
                     MyPrintDocument.Print();
                     break;
                 default:
                     break;
             }
             switch (e.KeyData)
             {
                 case Keys.F5: //Raporlar sayfasında F5 kısayol tuşu EXCEL'E GÖNDER  
                     ExceleGonder();
                     break;
                 default:
                     break;
             }

         }
         else if (tabControl1.SelectedIndex == 2) //servis ise 
         {
             switch (e.KeyData)
             {
                 case Keys.F2: //Servis sayfasında F2 kısayol tuşu Oda İsimlerini kaydetmek için
                     OdaIsimleriKaydet();      
                     break;
                 default:
                     break;
             }
         }
         else if (tabControl1.SelectedIndex == 3) //müşteriler ise 
         {
             switch (e.KeyData)
             {
                 case Keys.F9: //Müşteriler sayfasında F9 kısayol tuşu müşteri kaydet formunu açar
                     YeniMusteriKayıt();
                     break;
                 default:
                     break;
             }
             switch (e.KeyData)
             {
                 case Keys.F10: //Müşteriler sayfasında F10 kısayol tuşu müşteri bilgileri düzenle formunu açar 
                     MusteriDuzenle();
                     break;
                 default:
                     break;
             }
             switch (e.KeyData)
             {
                 case Keys.F11: //Müşteriler sayfasında F11 kısayol tuşu ile seçilen müşteriyi siler
                     MusteriSil();
                     break;
                 default:
                     break;
             }
         }
     }

