     private void backgroundWorker1_DoWork(object sender, DoWorkEventArgs e) // anamenün açılış anında backgroundWorker devreye girer
     {
         load();
         //int i = 1000;
         for (int i = 0; i < 1000; i++)
         {
          backgroundWorkerAnaMenuLoading.ReportProgress(i / 10); 
          System.Threading.Thread.Sleep(5);
         
         }

         if (frmAnaMenuIcınProgressBarLoading.progressBar1.Value.ToString() == "99")
         {
             frmAnaMenuIcınProgressBarLoading.resultLabel.Text = "Ana Menü Yükleniyor ... %100";
         }
         if (frmAnaMenuIcınProgressBarLoading.progressBar1.Value.ToString() == "100")
         {
             frmAnaMenuIcınProgressBarLoading.resultLabel.Text = "Ana Menü Yüklendi !!!";
         } 
         frmAnaMenuIcınProgressBarLoading.Close(); 
         this.Opacity = 100;
     }

